package loops;

public class Swapping {
	static int a=40,b=30;		
	public static void main(String[] args) {
		
	        ravi();
		// TODO Auto-generated method stub

	}
	static void ravi() {
		
		for(int d=0;d<1;d++) {
		      int c=a;
	              a=b;
			      b=c;
			 System.out.println("b"+"="+b);
			 System.out.println("a"+"="+a);
					
		}
	}
}

